extension.install:
if (!$forum_db->table_exists('questions'))
{
	$schema = array(
		'FIELDS' => array(
			'topic_id'		=> array(
				'datatype'		=> 'int(10)',
				'allow_null'	=> false
			),
			'question'			=> array(
				'datatype'		=> 'VARCHAR(200)',
				'allow_null'	=> false
			),
			'read_unvote_users'		=> array(
				'datatype'		=> 'tinyint(1)',
				'allow_null'	=> true
			),
			'revote'			=> array(
				'datatype'		=> 'tinyint(1)',
				'allow_null'	=> true
			),
			'created'	=> array(
				'datatype'		=> 'int(10)',
				'allow_null'	=> false
			),
			'days_count'	=> array(
				'datatype'		=> 'int(10)',
				'allow_null'	=> true
			),
			'votes_count'	=> array(
				'datatype'		=> 'int(10)',
				'allow_null'	=> true
			)
		),
		'PRIMARY KEY'	=>	array('topic_id')
	);
	$forum_db->create_table('questions', $schema);
}

if (!$forum_db->table_exists('voting'))
{
	$schema = array(
		'FIELDS'		=> array(
			'id'			=> array(
				'datatype'		=> 'SERIAL',
				'allow_null'	=> false
			),
			'topic_id'		=> array(
				'datatype'		=> 'int(10)',
				'allow_null'	=> false
			),
			'user_id'			=> array(
				'datatype'		=> 'int(10)',
				'allow_null'	=> false
			),
			'answer_id'			=> array(
				'datatype'		=> 'int(10)',
				'allow_null'	=> false
			)
		),
		'PRIMARY KEY'	=>	array('id')
	);
	$forum_db->create_table('voting', $schema);
}
if (!$forum_db->table_exists('answers'))
{
	$schema = array(
		'FIELDS'	=> array(
			'id'				=> array(
				'datatype'		=> 'SERIAL',
				'allow_null'	=> false
			),
			'topic_id'		=> array(
				'datatype'		=> 'int(10)',
				'allow_null'	=> false
			),
			'answer'			=> array(
				'datatype'		=> 'VARCHAR(100)',
				'allow_null'	=> true
			)
		),
		'PRIMARY KEY'	=>	array('id')
	);
	$forum_db->create_table('answers', $schema);
}

$forum_db->add_field('groups', 'g_poll_add', 'TINYINT(1)', false, 1);

$query_pun_poll = array(
	'SELECT'	=>	'1',
	'FROM'		=>	'config',
	'WHERE'		=>	'conf_name = "p_pun_poll_enable_read"'
);
$result_pun_poll = $forum_db->query_build($query_pun_poll) or error(__FILE__, __LINE__);

if ($forum_db->num_rows($result_pun_poll) == 0)
{
	$query_pun_poll = array(
		'INSERT'	=> 'conf_name, conf_value',
		'INTO'		=> 'config',
		'VALUES'	=> '"p_pun_poll_enable_read", "0"'
	);

	$forum_db->query_build($query_pun_poll) or error(__FILE__, __LINE__);
}

$query_pun_poll = array(
	'SELECT'	=>	'1',
	'FROM'		=>	'config',
	'WHERE'		=>	'conf_name = "p_pun_poll_enable_revote"'
);
$result_pun_poll = $forum_db->query_build($query_pun_poll) or error(__FILE__, __LINE__);

if ($forum_db->num_rows($result_pun_poll) == 0)
{
	$query_pun_poll = array(
		'INSERT'	=> 'conf_name, conf_value',
		'INTO'		=> 'config',
		'VALUES'	=> '"p_pun_poll_enable_revote", "0"'
	);

	$forum_db->query_build($query_pun_poll) or error(__FILE__, __LINE__);
}

$query_pun_poll = array(
	'SELECT'	=>	'1',
	'FROM'		=>	'config',
	'WHERE'		=>	'conf_name = "p_pun_poll_max_answers"'
);
$result_pun_poll = $forum_db->query_build($query_pun_poll) or error(__FILE__, __LINE__);

if ($forum_db->num_rows($result_pun_poll) == 0)
{
	$query_pun_poll = array(
		'INSERT'	=> 'conf_name, conf_value',
		'INTO'		=> 'config',
		'VALUES'	=> '"p_pun_poll_max_answers", "7"'
