		'WHERE'		=> 'topic_id IN('.$pun_poll_topic_ids.')'
	);
	$forum_db->query_build($query_poll) or error(__FILE__, __LINE__);

	$query_poll = array(
		'DELETE'	=> 'questions',
		'WHERE'		=> 'topic_id IN('.$pun_poll_topic_ids.')'
	);
	$forum_db->query_build($query_poll) or error(__FILE__, __LINE__);

	$query_poll = array(
		'DELETE'	=> 'answers',
		'WHERE'		=> 'topic_id IN('.$pun_poll_topic_ids.')'
	);
	$forum_db->query_build($query_poll) or error(__FILE__, __LINE__);
	unset($pun_poll_topic_ids);

